<?php class Data {function data_atual(){// leitura das datas$dia = date('d');$mes = date('m');$ano = date('Y');$hora =date("H:i:s");// definindo padr�o pt (dd/MM/YYYY)$data = "$dia-$mes-$ano $hora";return $data;}//2- Fun��o que retorna a data atual no padr�o Ingl�s (YYYY-MM-DD):function data_atual_en() {// leitura das datas$dia = date('d');$mes = date('m');$ano = date('Y');$hora =date("H:i:s");// definindo padr�o en (YYYY-MM-DD)$data_en = "$ano-$mes-$dia $hora";return $data_en;}function hora_atual() { $hora = date("H:i"); return $hora;}function hora_atual_completa() { $hora = date("H:i:s"); return $hora;}//5- FUN��O QUE VALIDA A DATA//Esta fun��o faz todas a verifica��es necess�rias como verificar se o m�s est� entre 1 e 12,  verificar se o dia est� dentro dos dias permitidos para aquele m�s (leva em considera��o os anos bissextos) e verificar se o ano � v�lido.//OBS.: As datas enviadas podem estar no formato ingl�s (en) ou brasileiro (pt).function valida_data($data, $tipo = "pt"){    if ($tipo == 'pt')	{		$d = explode("/", $data);		$dia = $d[0];		$mes = $d[1];		$ano = $d[2];	}else if ($tipo == 'en')	{		$d = explode("-",$data);		$dia = $d[2];		$mes = $d[1];		$ano = $d[0];	}	//usando fun��o checkdate para validar a data	if (checkdate($mes, $dia, $ano))	{		$data = $ano.'/'.$mes.'/'.$dia;		if (			//verificando se o ano tem 4 d�gitos			(strlen($ano) != '4') ||			//verificando se o m�s � menor que zero			($mes <= '0') ||                        //verificando se o m�s � maior que 12                        ($mes > '12') ||			//verificando se o dia � menor que zero			($dia <= '0') ||                        //verificando se o dia � maior que 31                        ($dia > '31')		    )		{			return false;		}		if (strlen($data) == 10)			return true;	}	else{		return false;	}}function data_user_para_mysql($y){    $data_inverter = explode("/",$y);    $x = $data_inverter[2].'-'. $data_inverter[1].'-'. $data_inverter[0];    return $x;}function data_mysql_para_user($y){	if ($y != '')	{ 		$x = implode("/",array_reverse(explode("-",$y)));		return $x;	}else{		return '';	}}}?>