<?php class Sql {        public $tabela = "";    public $dados;    public $valoresSql;    public $instrucaoSql;    public $camposTabelas;    public $condicoesTabela;    public $ordenacao;    public $colunaOrdenada;        public function __construct($tabela) { // construtor, nome da tabela como parametro        $this->tabela = $tabela;    }    public function sqlInserir() {        $tamanhoArray = count($this->dados);        $interacoes = 1;        foreach ($this->dados as $valor) {            if ($interacoes < $tamanhoArray) {                $this->valoresSql .= "'" . $valor . "', ";            } else {                $this->valoresSql .= "'" . $valor . "' ";            }            $interacoes++;        }        $this->instrucaoSql = "INSERT INTO {$this->tabela}() VALUES({$this->valoresSql})";        return $this->instrucaoSql;    }    public function sqlAtualizar($where = NULL) {          $tamanhoTabela = count($this->camposTabelas);        $interacoes = 0;        foreach ($this->camposTabelas as $campo) {                if ($interacoes < $tamanhoTabela-1 ) {                    $this->valoresSql .= $campo."='" . $this->dados[$interacoes] . "', ";                } else {                    $this->valoresSql .= $campo."='" . $this->dados[$interacoes] . "' ";                }                $interacoes++;                }        if ($where) {           $this->instrucaoSql = "UPDATE  " . $this->tabela . " SET ".$this->valoresSql." where ".$where;        } else {            $this->instrucaoSql = "UPDATE  " . $this->tabela . " SET ".$this->valoresSql;        }        return $this->instrucaoSql;    }    /**     * @method de monta sql de excluir     */    public function sqlexcluir() {             $this->instrucaoSql= "DELETE FROM " . $this->tabela;             if($this->condicoesTabela!=null){                $this->instrucaoSql .= " WHERE ";                foreach ($this->condicoesTabela as $condicoes) {                    $this->instrucaoSql .= $condicoes . " ";                }              }              return $this->instrucaoSql;    }    public function sqlPesquisar() {        $this->instrucaoSql = "SELECT ";        if (isset($this->camposTabelas)) {            $qtdCampos = count($this->camposTabelas);            $interacoesCampos = 1;            foreach ($this->camposTabelas as $campos) {                if ($interacoesCampos < $qtdCampos) {                    $this->instrucaoSql .= $campos . ", ";                } else {                    $this->instrucaoSql .= $campos . " FROM ";                }                $interacoesCampos++;            }        } else {            $this->instrucaoSql .= " * FROM ";        }        $qtdTabelas = count($this->tabela);        $interacoesTabelas = 1;        foreach ($this->tabela as $apelido => $nomeTb) {            if ($interacoesTabelas < $qtdTabelas) {                $this->instrucaoSql .= $nomeTb . " AS " . $apelido . ", ";            } else {                $this->instrucaoSql .= $nomeTb . " AS " . $apelido;            }            $interacoesTabelas++;        }        if($this->condicoesTabela!=null){        $this->instrucaoSql .= " WHERE ";        foreach ($this->condicoesTabela as $condicoes) {            $this->instrucaoSql .= $condicoes . " ";        }        }        if ($this->colunaOrdenada!=null) {            if ($this->ordenacao == "DESC") {                $this->instrucaoSql .= " ORDER BY " . $this->colunaOrdenada . " DESC ";            } else {                $this->instrucaoSql .= " ORDER BY " . $this->colunaOrdenada . " ASC ";            }        }        return $this->instrucaoSql;    }}?>