<?phpclass String {    private $String = "";    //Validar a String    function ValidarTexto($input) {        $codificado = htmlspecialchars(trim($input), ENT_QUOTES);        return $codificado;    }    //coloca a String toda minuscula    function ValidarTextoPraMinusculo($input) {        $codificado = htmlspecialchars(trim(strtolower($input)), ENT_QUOTES);        return $codificado;    }}?>