<?php class phpmailerUtil {     public $mail;     private $enviado;     function __construct() {// Inicia a classe PHPMailer        $this->mail = new PHPMailer(); // Define os dados do servidor e tipo de conexão         $this->mail->IsSMTP(); // Define que a mensagem será SMTP        $this->mail->Host = "br156.hostgator.com.br"; // Endereço do servidor SMTP        $this->mail->SMTPAuth = true; // Usa autenticação SMTP? (opcional)        $this->mail->Username = 'coopnes@coopnes.com.br'; // Usuário do servidor SMTP        $this->mail->Password = 'coopnes2014'; // Senha do servidor SMTP    }    public function I_Remetente_Destinatários($fromNome,$fromEmail,$toEmail, $toNome, $opDestinatários) {        if ($fromNome==null&&$fromEmail==NULL) {            $fromNome='teste';$fromEmail='coopnes@coopnes.com.br';        }// Define os destinatário(s)        if ($opDestinatários == 0) {// Define o remetente            $this->mail->From = $toEmail; // e-mail            $this->mail->FromName = $toNome; // nome            $this->mail->AddAddress($fromEmail, $fromNome); //$this->mail->AddAddress('');//$this->mail->AddCC('', ''); // Copia//$this->mail->AddBCC('', ''); // Cópia Oculta         }        if ($opDestinatários >= 1) {// Define o remetente            $this->mail->From = $fromEmail; // e-mail            $this->mail->FromName = $fromNome; // nome            $this->mail->AddAddress($toEmail, $toNome); //coopnes@coopnes.com0.br - COOPNES / atendimentocoopnes.com.br//$this->mail->AddAddress('');//$this->mail->AddCC('', ''); // Copia//$this->mail->AddBCC('', ''); // Cópia Oculta         }    }    public function IIConfigMsg($isHTML) {        // Define os dados técnicos da Mensagem        $this->mail->IsHTML($isHTML); // Define que o e-mail será enviado como HTML        $this->mail->CharSet = 'UTF-8'; // Charset da mensagem (opcional) UTF-8 ou iso-8859-1    }    public function IIIDefineMensagem($assunto, $ITexto) {        // Define a mensagem (Texto e Assunto)        $this->mail->Subject = $assunto; // Assunto da mensagem        $this->mail->Body = $ITexto;        $this->mail->AltBody = $ITexto . " \r\n ";// Define os anexos (opcional) //$this->mail->AddAttachment("c:/temp/documento.pdf", "novo_nome.pdf");  // Insere um anexo    }        public function IVEnviar() {// Envia o e-mail        $this->enviado = $this->mail->Send();// Limpa os destinatários e os anexos        $this->mail->ClearAllRecipients();        $this->mail->ClearAttachments();// Exibe uma mensagem de resultado         if ($this->enviado) {            return true;        } else {            return false;        }    }}?>